import React from "react";
import "./messagecard.css";

class MessageCard extends React.Component {
  getTime = () => {
    let d = new Date();
    let hours = d.getHours();
    let minutes = d.getMinutes();
    let time = minutes + hours;
    return time;
  };

  render() {
    return (
      <div
        className={
          this.props.username === this.props.author ? "card-right" : "card-left"
        }
      >
        <div className="author-style"> {this.props.author}</div>
        <div className="message-style">{this.props.message}</div>
        <div className="date-style">{this.getTime()}</div>
      </div>
    );
  }
}

export default MessageCard;
