import React from "react";
import ReactDOM from "react-dom";
import Style from "./login.css";

class Login extends React.Component {
  state = { term: "", isAuthenticated: false };

  onFormSubmit = (event) => {
    this.setState({ term: `batata`, isAuthenticated: `true` });
    console.log(this.state.isAuthenticated);
  };

  render() {
    if (this.isAuthenticated === true) {
      return <div>dae {this.state.term}</div>;
    } else
      return (
        <div className="login">
          <section
            className="masthead"
            role="img"
            aria-label="Image Description"
          >
            <h1>GARDN.IO</h1>
            <form onSubmit={this.onFormSubmit} className="ui form">
              <div className="ui action input">
                <input
                  type="text"
                  value={this.state.term}
                  onChange={(e) => this.setState({ term: e.target.value })}
                  placeholder="Insira seu nome"
                ></input>
                <button className="ui button">Entrar</button>
              </div>
            </form>
            <div className="space"></div>
            <div className="visitante">
              <a href="/">ou clique aqui para entrar como visitante</a>
            </div>
          </section>
        </div>
      );
  }
}

export default Login;
