import React from "react";

import "./login.css";

class Login extends React.Component {
  state = { term: "" };

  onFormSubmit = (event) => {
    this.props.authenticator(this.state.term);
  };

  onDivClick = (event) => {
    this.props.authenticator("visitante");
  };

  render() {
    return (
      <div className="login">
        <section className="masthead" role="img" aria-label="Image Description">
          <h1>GARDN.IO</h1>
          <form onSubmit={this.onFormSubmit} className="ui form">
            <div className="ui action input">
              <input
                type="text"
                value={this.state.term}
                onChange={(e) => this.setState({ term: e.target.value })}
                placeholder="Insira seu nome"
              ></input>
              <button className="ui button">Entrar</button>
            </div>
          </form>
          <div className="space"></div>
          <div onClick={this.onDivClick} className="visitante">
            <a href="/">ou clique aqui para entrar como visitante</a>
          </div>
        </section>
      </div>
    );
  }
}

export default Login;
